/* tool tip style */
.d3-tip {
    line-height: 1;
    font-family: 'Averia Libre', 'cursive';
    font-weight: bold;
    padding: 8px;
    background: rgba(89,99,129, 0.9);
    color: #fff;
    border-radius: 2px;
    pointer-events: none;
  }

  /* Creates a small triangle extender for the tooltip */
  .d3-tip:after {
    box-sizing: border-box;
    display: inline;
    font-size: 10px;
    width: 100%;
    line-height: 1;
    color: rgba(0, 0, 0, 0.8);
    position: absolute;
    pointer-events: none;
  }

  /* Northward tooltips */
  .d3-tip.n:after {
    content: "\25BC";
    margin: -1px 0 0 0;
    top: 100%;
    left: 0;
    text-align: center;
  }


